import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { Tablet } from "../Responsive";
import Perks from "./Perks";
import axios from "axios";

const Wrapper = styled.div`
  label {
    font-weight: 500;
  }
  .photos {
    border: 2px solid black;
  }
  .img-link {
    display: grid;
    grid-template-columns: 80% 20%;
  }
  .perks {
    display: grid;
    gap: 5px;
    grid-template-columns: auto;
    ${Tablet({ gridTemplateColumns: "auto auto auto" })}
  }
  .perk-item {
    display: flex;
    border: 1px solid gray;
    align-items: center;
    padding: 10px;
    border-radius: 10px;
  }
  .perks .icon {
    margin: 10px 0;
  }
  .check-info {
    display: flex;
    flex-direction: column;
    ${Tablet({ flexDirection: "row" })}
  }
`;

function PlacesPage() {
  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [photos, setPhotos] = useState([]);
  const [photoLink, setPhotoLink] = useState("");
  const [description, setDescription] = useState("");
  const [perks, setPerks] = useState([]);
  const [extraInfo, setExtraInfo] = useState("");
  const [checkIn, setCheckIn] = useState("");
  const [checkOut, setCheckOut] = useState("");
  const [maxGuest, setMaxGuest] = useState(1);

  const addPhotoByLink = async (e) => {
    e.preventDefault();
    const { data: filename } = axios.post("/uplaodbylink", { link: photoLink });
    setPhotos((prev) => {
      return [...prev, filename];
    });
    setPhotoLink("");
  };

  const uploadPhotos = (e) => {
    const { files } = e.target;
    let data = new FormData();
    for (let i = 0; i < files.length; i++) {
      data.append("photos", files[i]);
    }
    axios
      .post("/upload", data, {
        headers: { "Content-type": "multipart/form-data" },
      })
      .then((resp) => {
        const { data: filenames } = resp;
        setPhotos((prev) => {
          return [...prev, ...filenames];
        });
        console.log(filenames);
      })
      .catch((err) => console.log(err));
    // console.log(files);
  };

  return (
    <Wrapper className="container">
      <h3>Accommodation</h3>
      <Link to="/account" className="btn btn-primary my-2">
        &plus, Add new place
      </Link>
      <div>
        <form>
          <label className="form-label my-2">Title</label>
          <p className="text-secondary">your title should be short</p>
          <input
            type="text"
            name="title"
            className="form-control"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="title"
          />
          <label className="form-label my-2">Address</label>
          <p className="text-secondary">address to this place</p>
          <input
            type="text"
            name="address"
            className="form-control"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            placeholder="address"
          />
          <label className="form-label my-2">Photos</label>
          <p className="text-secondary">the more = better</p>
          <div className="img-link">
            <input
              type="text"
              name="photoLink"
              className="form-control"
              value={photoLink}
              onChange={(e) => setPhotoLink(e.target.value)}
              placeholder={"add using a link...jpg"}
            />
            <button className="btn btn-secondary" onClick={addPhotoByLink}>
              Add photos
            </button>
          </div>

          {/* {photos.length > 0 &&
            photos.map((link, i) => (
              <div key={i}>
                <div className="img-container">
                  <img src={`uploads/${link}`} alt="cover" />
                </div>
              </div>
            ))} */}

          <br />
          <label name="photos" className="btn photos my-2">
            <input
              type="file"
              multiple
              className="hidden"
              onChange={uploadPhotos}
            />
            <i className="fa fa-upload"></i> Upload
          </label>
          <br />
          <div>
            <h2>Description</h2>
            <p>Description of the place</p>
            <textarea
              className="form-control"
              rows="5"
              name="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </div>
          <div>
            <Perks perks={perks} setPerks={setPerks} />
          </div>
          <div>
            <h2>ExtraInfo</h2>
            <p>house rules etc</p>
            <textarea
              className="form-control"
              name="extraInfo"
              value={extraInfo}
              onChange={(e) => setExtraInfo(e.target.value)}
              rows="5"
            />
          </div>
          <div>
            <h2>CheckIn and CheckOut times, max Guests</h2>
            <p>
              add check in and check out time, remember to have some time window
              for cleaning the room
            </p>
            <div className="check-info">
              <div className="me-2">
                <h4>Check in time</h4>
                <input
                  type="text"
                  placeholder="14:00"
                  name="checkIn"
                  value={checkIn}
                  onChange={(e) => setCheckIn(e.target.value)}
                />
              </div>
              <div className="me-2">
                <h4>Check out time</h4>
                <input
                  type="text"
                  placeholder="16:00"
                  name="checkOut"
                  value={checkOut}
                  onChange={(e) => setCheckOut(e.target.value)}
                />
              </div>
              <div className="me-2">
                <h4>Max number Guest</h4>
                <input
                  type="number"
                  placeholder="1.0"
                  name="maxGuest"
                  value={maxGuest}
                  onChange={(e) => setMaxGuest(e.target.value)}
                />
              </div>
            </div>
          </div>
          <button className="btn btn-primary my-3 form-control" type="submit">
            save
          </button>
        </form>
      </div>
    </Wrapper>
  );
}

export default PlacesPage;
