import React, { useContext, useState } from "react";
import { Link, Navigate, useNavigate, Outlet } from "react-router-dom";
import styled from "styled-components";
import { UserContext } from "../context/userContext";
import axios from "axios";

const Wrapper = styled.div`
  margin: 0;

  nav {
    display: flex;
    justify-content: center;
  }
`;

function SharedProfileLayout() {
  const navigate = useNavigate();
  const { ready, user, setUser } = useContext(UserContext);
  const [redirect, setRedirect] = useState(null);

  if (!ready) {
    return "loading...";
  }
  if (ready && !user) {
    navigate("/login");
  }

  const logout = async () => {
    await axios.post("/logout");
    setRedirect("/");
    setUser(null);
  };

  return (
    <Wrapper>
      <nav className="mt-4 gap-2">
        <Link className="" to={"/account"}>
          Profile
        </Link>
        <Link className="" to={"/account/bookings"}>
          My Bookings
        </Link>
        <Link className="" to={"/account/places"}>
          My Accommodations
        </Link>
      </nav>
      <div></div>
      <div className="dashboard-pages">
        <Outlet />
      </div>
    </Wrapper>
  );
}

export default SharedProfileLayout;
