import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { Tablet } from "../Responsive";

const Wrapper = styled.div`
  label {
    font-weight: 500;
  }
  .photos {
    border: 2px solid black;
  }
  .img-link {
    display: grid;
    grid-template-columns: 80% 20%;
  }
  .perks {
    display: grid;
    gap: 5px;
    grid-template-columns: auto;
    ${Tablet({ gridTemplateColumns: "auto auto auto" })}
  }
  .perk-item {
    display: flex;
    border: 1px solid gray;
    align-items: center;
    padding: 10px;
    border-radius: 10px;
  }
  .perks .icon {
    margin: 10px 0;
  }
  .check-info {
    display: flex;
    flex-direction: column;
    ${Tablet({ flexDirection: "row" })}
  }
`;

function PlacesPage() {
  const [title, setTitle] = useState("");
  const [address, setAddress] = useState("");
  const [photos, setPhotos] = useState([]);
  const [description, setDescription] = useState("");
  const [perks, setperks] = useState([]);
  const [extraInfo, setExtraInfo] = useState("");
  const [checkIn, setCheckIn] = useState("");
  const [checkOut, setCheckOut] = useState("");
  const [maxGuest, setMaxGuest] = useState(1);

  return (
    <Wrapper className="container">
      <h3>Accommodation</h3>
      <Link to="/account" className="btn btn-primary my-2">
        &plus, Add new place
      </Link>
      <div>
        <form>
          <label className="form-label my-2">Title</label>
          <p className="text-secondary">your title should be short</p>
          <input
            type="text"
            name="title"
            className="form-control"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="title"
          />
          <label className="form-label my-2">Address</label>
          <p className="text-secondary">address to this place</p>
          <input
            type="text"
            name="address"
            className="form-control"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            placeholder="address"
          />
          <label className="form-label my-2">Photos</label>
          <p className="text-secondary">the more = better</p>
          <div className="img-link">
            <input
              type="text"
              name="photos"
              className="form-control"
              value={photos}
              onChange={(e) => setPhotos(e.target.value)}
              placeholder={"add using a link...jpg"}
            />
            <button className="btn btn-secondary">Add photos</button>
          </div>
          <br />
          <button
            type="file"
            name="photos"
            className="btn photos my-2"
            value={photos}
            onChange={(e) => setPhotos(e.target.value)}
          >
            <i className="fa fa-upload"></i> upload from file
          </button>
          <br />
          <div>
            <h2>Description</h2>
            <p>Description of the place</p>
            <textarea className="form-control" rows="5" />
          </div>
          <div>
            <h2>Perks</h2>
            <p>Select all Perks</p>
            <div className="perks">
              <label className="perk-item">
                <input type="checkbox" />
                <i className="fa-solid fa-wifi icon"></i>
                <span>Wifi</span>
              </label>
              <label className="perk-item">
                <input type="checkbox" />
                <i className="fa-soild fa-car icon"></i>
                <span>free parking spot</span>
              </label>
              <label className="perk-item">
                <input type="checkbox" />
                <i className="fa-solid fa-tv icon"></i>
                <span>TV</span>
              </label>
              <label className="perk-item">
                <input type="checkbox" />
                <i className="fa-soild fa-signal icon"></i>
                <span>Radio</span>
              </label>
              <label className="perk-item">
                <input type="checkbox" />
                <i className="fa fa-thumbs-up icon"></i>
                <span>Pets</span>
              </label>
              <label className="perk-item">
                <input type="checkbox" />
                <i className="fa-solid fa-arrow-right icon"></i>
                <span>Private entrance</span>
              </label>
            </div>
          </div>
          <div>
            <h2>ExtraInfo</h2>
            <p>house rules etc</p>
            <textarea
              className="form-control"
              name="extraInfo"
              value={extraInfo}
              onChange={(e) => setExtraInfo(e.target.value)}
              rows="5"
            />
          </div>
          <div>
            <h2>CheckIn and CheckOut times, max Guests</h2>
            <p>
              add check in and check out time, remeber to have some time window
              for cleaning the room
            </p>
            <div className="check-info">
              <div className="me-2">
                <h4>Check in time</h4>
                <input
                  type="number"
                  placeholder="14:00"
                  name="checkIn"
                  value={checkIn}
                  onChange={(e) => setCheckIn(e.target.value)}
                />
              </div>
              <div className="me-2">
                <h4>Check out time</h4>
                <input
                  type="number"
                  placeholder="16:00"
                  name="checkOut"
                  value={checkOut}
                  onChange={(e) => setCheckOut(e.target.value)}
                />
              </div>
              <div className="me-2">
                <h4>Max number Guest</h4>
                <input
                  type="number"
                  placeholder="1.0"
                  name="maxGuest"
                  value={maxGuest}
                  onChange={(e) => setMaxGuest(e.target.value)}
                />
              </div>
            </div>
          </div>
          <button className="btn btn-primary my-3 form-control" type="submit">
            save
          </button>
        </form>
      </div>
    </Wrapper>
  );
}

export default PlacesPage;
